(function($){Drupal.behaviors.getlocations_preview={attach:function(){if(typeof Drupal.settings.getlocations==='undefined'){return}$.each(Drupal.settings.getlocations,function(key,settings){if(settings.extcontrol=='preview_map'){google.maps.event.addListener(Drupal.getlocations_map[key],'zoom_changed',function(){$("#edit-getlocations-default-zoom").val(Drupal.getlocations_map[key].getZoom())});google.maps.event.addListener(Drupal.getlocations_map[key],'center_changed',function(){var ll=Drupal.getlocations_map[key].getCenter();$("#edit-getlocations-default-latlong").val(ll.lat()+','+ll.lng())});google.maps.event.addListener(Drupal.getlocations_map[key],'maptypeid_changed',function(){var maptype=Drupal.getlocations_map[key].getMapTypeId();if(maptype==google.maps.MapTypeId.ROADMAP){maptype='Map'}else if(maptype==google.maps.MapTypeId.SATELLITE){maptype='Satellite'}else if(maptype==google.maps.MapTypeId.HYBRID){maptype='Hybrid'}else if(maptype==google.maps.MapTypeId.TERRAIN){maptype='Physical'}else if(maptype=="OSM"){maptype='OpenStreetMap'}$("#edit-getlocations-default-maptype").val(maptype)})}})}}})(jQuery);