(function($){Drupal.behaviors.getlocations_fields_preview={attach:function(){$.each(Drupal.settings.getlocations,function(key,settings){google.maps.event.addListener(Drupal.getlocations_map[key],'zoom_changed',function(){$("#edit-field-settings-zoom").val(Drupal.getlocations_map[key].getZoom())});google.maps.event.addListener(Drupal.getlocations_map[key],'center_changed',function(){var ll=Drupal.getlocations_map[key].getCenter();$("#edit-field-settings-latlong").val(ll.lat()+','+ll.lng())});google.maps.event.addListener(Drupal.getlocations_map[key],'maptypeid_changed',function(){var maptype=Drupal.getlocations_map[key].getMapTypeId();if(maptype==google.maps.MapTypeId.ROADMAP){maptype='Map'}else if(maptype==google.maps.MapTypeId.SATELLITE){maptype='Satellite'}else if(maptype==google.maps.MapTypeId.HYBRID){maptype='Hybrid'}else if(maptype==google.maps.MapTypeId.TERRAIN){maptype='Physical'}else if(maptype=="OSM"){maptype='OpenStreetMap'}$("#edit-field-settings-maptype").val(maptype)});if(settings.input_map_show!=1){$("div.getlocations_fields_map_wrapper").addClass('js-hide')}})}}})(jQuery);